import { useState, useEffect } from 'react';

export default function FormularioContrato({ contrato = {}, onSalvar, onCancelar }) {
  const contratoInicial = {
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  };

  const [dados, setDados] = useState(contratoInicial);

  useEffect(() => {
    if (contrato) {
      setDados({ ...contrato });
    }
  }, [contrato]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    const atualizado = { ...dados, [name]: val };

    if (name === 'valorGlobal') {
      const num = parseFloat(val.replace(',', '.')) || 0;
      atualizado.valorComissao = (num * 0.04).toFixed(2);
    }

    setDados(atualizado);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSalvar(dados);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="max-w-4xl mx-auto bg-gray-800 rounded-xl shadow p-6">
        <h2 className="text-2xl font-bold mb-6">{contrato?.id ? 'Editar Contrato' : 'Novo Contrato'}</h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Input label="Cliente" name="cliente" value={dados.cliente} onChange={handleChange} />
          <div>
            <label className="block mb-1">Estado</label>
            <select
              name="estado"
              value={dados.estado}
              onChange={handleChange}
              className="w-full p-2 rounded bg-gray-700 border border-gray-600 text-white"
            >
              <option value="">Selecione...</option>
              {["AC", "AL", "AP", "AM", "BA", "CE", "DF", "ES", "GO", "MA", "MT", "MS", "MG", "PA", "PB", "PR", "PE", "PI", "RJ", "RN", "RS", "RO", "RR", "SC", "SP", "SE", "TO"].map(uf => (
                <option key={uf} value={uf}>{uf}</option>
              ))}
            </select>
          </div>
          <Input label="Valor Global (R$)" name="valorGlobal" value={dados.valorGlobal} onChange={handleChange} />
          <Input label="Comissão (4%)" name="valorComissao" value={dados.valorComissao} disabled />
          <Input label="Objeto" name="objeto" value={dados.objeto} onChange={handleChange} />
          <Input label="Qtd Elevadores" name="qtdElevadores" value={dados.qtdElevadores} onChange={handleChange} />
          <Input label="Qtd Plataformas" name="qtdPlataformas" value={dados.qtdPlataformas} onChange={handleChange} />
          <Input label="Início" name="inicio" type="date" value={dados.inicio} onChange={handleChange} />
          <Input label="Encerramento" name="encerramento" type="date" value={dados.encerramento} onChange={handleChange} />
          <Input label="Gestor" name="gestor" value={dados.gestor} onChange={handleChange} />
          <Input label="Telefone" name="telefone" value={dados.telefone} onChange={handleChange} />
          <Input label="Email" name="email" value={dados.email} onChange={handleChange} />

          <div className="col-span-1 md:col-span-2">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                name="necessitaArt"
                checked={dados.necessitaArt}
                onChange={handleChange}
              />
              Necessita ART
            </label>
          </div>

          <div className="col-span-1 md:col-span-2 flex gap-4 mt-4">
            <button type="submit" className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded-xl text-white font-semibold">
              Salvar
            </button>
            <button type="button" onClick={onCancelar} className="bg-gray-500 hover:bg-gray-600 px-4 py-2 rounded-xl text-white font-semibold">
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

function Input({ label, name, value, onChange, type = 'text', disabled = false }) {
  return (
    <div>
      <label className="block mb-1">{label}</label>
      <input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        disabled={disabled}
        className="w-full p-2 rounded bg-gray-700 border border-gray-600 text-white"
      />
    </div>
  );
}
