import { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, getDocs, deleteDoc, doc } from 'firebase/firestore';

export default function ListaContratos({ onVoltar, onEditar }) {
  const [contratos, setContratos] = useState([]);
  const [filtro, setFiltro] = useState('');

  useEffect(() => {
    carregarContratos();
  }, []);

  const carregarContratos = async () => {
    try {
      const contratosRef = collection(db, 'contratos');
      const snapshot = await getDocs(contratosRef);
      const lista = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setContratos(lista);
    } catch (error) {
      console.error('Erro ao carregar contratos:', error);
    }
  };

  const excluirContrato = async (id) => {
    const confirmar = window.confirm('Tem certeza que deseja excluir este contrato?');
    if (!confirmar) return;

    try {
      await deleteDoc(doc(db, 'contratos', id));
      alert('Contrato excluído com sucesso.');
      carregarContratos(); // Recarrega a lista após exclusão
    } catch (error) {
      console.error('Erro ao excluir contrato:', error);
      alert('Erro ao excluir contrato.');
    }
  };

  const contratosFiltrados = contratos.filter((c) =>
    (c.cliente + c.estado).toLowerCase().includes(filtro.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">Contratos Cadastrados</h1>

        <div className="mb-4">
          <input
            type="text"
            placeholder="Buscar por cliente ou estado..."
            value={filtro}
            onChange={(e) => setFiltro(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
          />
        </div>

        <div className="overflow-x-auto bg-white shadow rounded-lg">
          <table className="min-w-full border text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-3 py-2 border">Cliente</th>
                <th className="px-3 py-2 border">Estado</th>
                <th className="px-3 py-2 border">Valor Global</th>
                <th className="px-3 py-2 border">Comissão</th>
                <th className="px-3 py-2 border">Início</th>
                <th className="px-3 py-2 border">Encerramento</th>
                <th className="px-3 py-2 border">Ações</th>
              </tr>
            </thead>
            <tbody>
              {contratosFiltrados.map((c) => (
                <tr key={c.id} className="text-center">
                  <td className="px-3 py-2 border">{c.cliente}</td>
                  <td className="px-3 py-2 border">{c.estado}</td>
                  <td className="px-3 py-2 border">
                    {parseFloat(c.valorGlobal).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}
                  </td>
                  <td className="px-3 py-2 border">
                    {parseFloat(c.valorComissao).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}
                  </td>
                  <td className="px-3 py-2 border">{c.inicio}</td>
                  <td className="px-3 py-2 border">{c.encerramento}</td>
                  <td className="px-3 py-2 border space-x-2">
                    <button
                      onClick={() => onEditar(c)}
                      className="bg-yellow-400 hover:bg-yellow-500 text-white px-2 py-1 rounded text-xs"
                    >
                      Editar
                    </button>
                    <button
                      onClick={() => excluirContrato(c.id)}
                      className="bg-red-500 hover:bg-red-600 text-white px-2 py-1 rounded text-xs"
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))}
              {contratosFiltrados.length === 0 && (
                <tr>
                  <td colSpan="7" className="px-4 py-2 text-center text-gray-500">
                    Nenhum contrato encontrado.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <div className="mt-6">
          <button
            onClick={onVoltar}
            className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-xl"
          >
            Voltar para Cadastro
          </button>
        </div>
      </div>
    </div>
  );
}
