import { db } from './firebase';
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  Timestamp,
} from 'firebase/firestore';
import ListaContratos from './ListaContratos';
import { useState } from 'react';
import DetalhesContrato from './DetalhesContrato';
import FormularioContrato from './FormularioContrato';

export default function App() {
  const [modo, setModo] = useState('cadastro'); // cadastro | lista | detalhes
  const [contratoSelecionado, setContratoSelecionado] = useState(null);
  const [editandoContrato, setEditandoContrato] = useState(null);

  const contratoInicial = {
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  };

  const [contrato, setContrato] = useState(contratoInicial);

  // Atualiza o estado contrato conforme mudanças no formulário
  const onChangeContrato = (name, value) => {
    const updatedContrato = { ...contrato, [name]: value };

    if (name === 'valorGlobal') {
      const num = parseFloat(value.replace(',', '.')) || 0;
      updatedContrato.valorComissao = (num * 0.04).toFixed(2);
    }

    setContrato(updatedContrato);
  };

  // Salvar ou atualizar contrato no Firestore
  const handleSave = async () => {
    try {
      const dadosContrato = {
        ...contrato,
        valorGlobal: parseFloat(contrato.valorGlobal.replace(',', '.')),
        valorComissao: parseFloat(contrato.valorComissao),
        dataCriacao: Timestamp.now(),
      };

      if (editandoContrato) {
        const ref = doc(db, 'contratos', editandoContrato.id);
        await updateDoc(ref, dadosContrato);
        alert('Contrato atualizado com sucesso!');
      } else {
        await addDoc(collection(db, 'contratos'), dadosContrato);
        alert('Contrato salvo com sucesso!');
      }

      setContrato(contratoInicial);
      setEditandoContrato(null);
      setContratoSelecionado(null);
      setModo('lista');
    } catch (error) {
      console.error('Erro ao salvar contrato:', error);
      alert('Erro ao salvar contrato');
    }
  };

  // Quando editar um contrato existente
  const iniciarEdicao = (contrato) => {
    setContrato({
      ...contrato,
      valorGlobal: contrato.valorGlobal.toString(),
      valorComissao: contrato.valorComissao.toString(),
    });
    setEditandoContrato(contrato);
    setContratoSelecionado(contrato);
    setModo('cadastro');
  };

  if (modo === 'cadastro') {
    return (
      <FormularioContrato
        contrato={contrato}
        onChangeContrato={onChangeContrato}
        onSalvar={handleSave}
        onCancelar={() => {
          setModo('lista');
          setContrato(contratoInicial);
          setEditandoContrato(null);
          setContratoSelecionado(null);
        }}
      />
    );
  }

  if (modo === 'detalhes') {
    return (
      <DetalhesContrato
        contrato={contratoSelecionado}
        onVoltar={() => setModo('lista')}
        onEditar={iniciarEdicao}
      />
    );
  }

  if (modo === 'lista') {
    return (
      <ListaContratos
        onVoltar={() => {
          setModo('cadastro');
          setContrato(contratoInicial);
          setEditandoContrato(null);
          setContratoSelecionado(null);
        }}
        onEditar={iniciarEdicao}
        onVer={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('detalhes');
        }}
      />
    );
  }

  return null; // fallback
}
