import { useState, useEffect } from 'react';

const contratoInicial = {
  cliente: '',
  estado: '',
  valorGlobal: '',
  valorComissao: '',
  objeto: '',
  qtdElevadores: '',
  qtdPlataformas: '',
  inicio: '',
  encerramento: '',
  gestor: '',
  telefone: '',
  email: '',
  necessitaArt: false,
};

export default function FormularioContrato({ contrato, onSalvar, onCancelar }) {
  const [formContrato, setFormContrato] = useState(contratoInicial);

  useEffect(() => {
    if (contrato) {
      setFormContrato({
        ...contrato,
        // Garante que valores numÃ©ricos estejam em string para input
        valorGlobal: contrato.valorGlobal ? contrato.valorGlobal.toString() : '',
        valorComissao: contrato.valorComissao ? contrato.valorComissao.toString() : '',
      });
    } else {
      setFormContrato(contratoInicial);
    }
  }, [contrato]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;

    let updatedContrato = { ...formContrato, [name]: val };

    if (name === 'valorGlobal') {
      const num = parseFloat(val.replace(',', '.')) || 0;
      updatedContrato.valorComissao = (num * 0.04).toFixed(2);
    }

    setFormContrato(updatedContrato);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSalvar(formContrato);
  };

  return (
    <main className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">
          {contrato ? 'Editar Contrato' : 'Cadastro de Contrato'}
        </h1>

        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Input label="ÃrgÃ£o / Cliente" name="cliente" value={formContrato.cliente} onChange={handleChange} required />

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Estado</label>
            <select
              name="estado"
              value={formContrato.estado}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
              required
            >
              <option value="">Selecione um estado</option>
              <option value="AC">Acre</option>
              <option value="AL">Alagoas</option>
              <option value="AP">AmapÃ¡</option>
              <option value="AM">Amazonas</option>
              <option value="BA">Bahia</option>
              <option value="CE">CearÃ¡</option>
              <option value="DF">Distrito Federal</option>
              <option value="ES">EspÃ­rito Santo</option>
              <option value="GO">GoiÃ¡s</option>
              <option value="MA">MaranhÃ£o</option>
              <option value="MT">Mato Grosso</option>
              <option value="MS">Mato Grosso do Sul</option>
              <option value="MG">Minas Gerais</option>
              <option value="PA">ParÃ¡</option>
              <option value="PB">ParaÃ­ba</option>
              <option value="PR">ParanÃ¡</option>
              <option value="PE">Pernambuco</option>
              <option value="PI">PiauÃ­</option>
              <option value="RJ">Rio de Janeiro</option>
              <option value="RN">Rio Grande do Norte</option>
              <option value="RS">Rio Grande do Sul</option>
              <option value="RO">RondÃ´nia</option>
              <option value="RR">Roraima</option>
              <option value="SC">Santa Catarina</option>
              <option value="SP">SÃ£o Paulo</option>
              <option value="SE">Sergipe</option>
              <option value="TO">Tocantins</option>
            </select>
          </div>

          <Input label="Valor Global (R$)" name="valorGlobal" value={formContrato.valorGlobal} onChange={handleChange} required />

          <Input label="Valor da ComissÃ£o (4%)" name="valorComissao" value={formContrato.valorComissao} disabled />

          <Input label="Objeto do Contrato" name="objeto" value={formContrato.objeto} onChange={handleChange} required />

          <Input label="Quantidade de Elevadores" name="qtdElevadores" value={formContrato.qtdElevadores} onChange={handleChange} />

          <Input label="Quantidade de Plataformas" name="qtdPlataformas" value={formContrato.qtdPlataformas} onChange={handleChange} />

          <Input label="Data de InÃ­cio" name="inicio" type="date" value={formContrato.inicio} onChange={handleChange} required />

          <Input label="Data de Encerramento" name="encerramento" type="date" value={formContrato.encerramento} onChange={handleChange} required />

          <Input label="Gestor (opcional)" name="gestor" value={formContrato.gestor} onChange={handleChange} />

          <Input label="Telefone (opcional)" name="telefone" value={formContrato.telefone} onChange={handleChange} />

          <Input label="E-mail (opcional)" name="email" value={formContrato.email} onChange={handleChange} />

          <div className="col-span-1 md:col-span-2 flex items-center gap-2">
            <input
              type="checkbox"
              name="necessitaArt"
              checked={formContrato.necessitaArt}
              onChange={handleChange}
              id="necessitaArt"
            />
            <label htmlFor="necessitaArt" className="text-sm font-medium text-gray-700">Necessita de ART</label>
          </div>

          <div className="col-span-1 md:col-span-2 mt-4 flex gap-4">
            <button
              type="submit"
              className="flex-1 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-xl"
            >
              {contrato ? 'Atualizar Contrato' : 'Salvar Contrato'}
            </button>
            <button
              type="button"
              onClick={onCancelar}
              className="flex-1 bg-gray-300 hover:bg-gray-400 text-black font-semibold py-2 px-4 rounded-xl"
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </main>
  );
}

function Input({ label, name, value, onChange, type = 'text', disabled = false }) {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor={name}>{label}</label>
      <input
        id={name}
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        disabled={disabled}
        className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
      />
    </div>
  );
}
