import { useState } from 'react';
import FormularioContrato from './FormularioContrato';
import ListaContratos from './ListaContratos';
import DetalhesContrato from './DetalhesContrato';

export default function App() {
  // Estado para controlar a tela atual: 'cadastro', 'lista' ou 'detalhes'
  const [modo, setModo] = useState('cadastro');

  // Contrato selecionado para editar ou visualizar detalhes
  const [contratoSelecionado, setContratoSelecionado] = useState(null);

  return (
    <div>
      {modo === 'cadastro' && (
        <FormularioContrato
          contrato={contratoSelecionado}
          onSalvar={() => {
            setModo('lista');
            setContratoSelecionado(null);
          }}
          onCancelar={() => {
            setModo('lista');
            setContratoSelecionado(null);
          }}
        />
      )}

      {modo === 'lista' && (
        <ListaContratos
          onVoltar={() => setModo('cadastro')}
          onEditar={(contrato) => {
            setContratoSelecionado(contrato);
            setModo('cadastro');
          }}
          onVer={(contrato) => {
            setContratoSelecionado(contrato);
            setModo('detalhes');
          }}
        />
      )}

      {modo === 'detalhes' && (
        <DetalhesContrato
          contrato={contratoSelecionado}
          onVoltar={() => setModo('lista')}
          onEditar={(contrato) => {
            setContratoSelecionado(contrato);
            setModo('cadastro');
          }}
        />
      )}
    </div>
  );
}
