import { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, onSnapshot } from 'firebase/firestore';

export default function Dashboard() {
  const [contratos, setContratos] = useState([]);

  useEffect(() => {
    const contratosRef = collection(db, 'contratos');
    const unsubscribe = onSnapshot(contratosRef, (snapshot) => {
      const lista = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setContratos(lista);
    });
    return () => unsubscribe();
  }, []);

  // Filtros e cálculos:
  const mesAtual = new Date().getMonth() + 1; // 1-12
  const anoAtual = new Date().getFullYear();

  const contratosAtivos = contratos.filter(c => {
    if (!c.inicio || !c.encerramento) return false;
    const inicio = new Date(c.inicio);
    const fim = new Date(c.encerramento);
    const hoje = new Date();
    return inicio <= hoje && fim >= hoje;
  });

  const totalVendasMes = contratos.reduce((acc, c) => {
    if (!c.inicio) return acc;
    const dataInicio = new Date(c.inicio);
    if (dataInicio.getMonth() + 1 === mesAtual && dataInicio.getFullYear() === anoAtual) {
      return acc + (parseFloat(c.valorGlobal) || 0);
    }
    return acc;
  }, 0);

  const totalComissao = contratos.reduce((acc, c) => acc + (parseFloat(c.valorComissao) || 0), 0);

  const totalElevadores = contratos.reduce((acc, c) => acc + (parseInt(c.qtdElevadores) || 0), 0);
  const totalPlataformas = contratos.reduce((acc, c) => acc + (parseInt(c.qtdPlataformas) || 0), 0);

  function Card({ title, value, isCurrency = false }) {
    return (
      <div className="bg-gray-800 rounded-xl p-6 shadow flex flex-col items-center">
        <h3 className="text-gray-400 mb-2">{title}</h3>
        <p className="text-3xl font-bold text-white">
          {isCurrency ? value.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' }) : value}
        </p>
      </div>
    );
  }

  return (
    <div className="bg-gray-900 text-white p-6 rounded-xl shadow mb-8">
      <h1 className="text-4xl font-bold mb-8 text-center">Gestão dos Contratos</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-6">
        <Card title="Total Vendas no Mês" value={totalVendasMes} isCurrency />
        <Card title="Contratos Ativos" value={contratosAtivos.length} />
        <Card title="Comissões Totais" value={totalComissao} isCurrency />
        <Card title="Total de Elevadores" value={totalElevadores} />
        <Card title="Total de Plataformas" value={totalPlataformas} />
      </div>
    </div>
  );
}
