import { db } from './firebase';
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  Timestamp,
} from 'firebase/firestore';
import ListaContratos from './ListaContratos';
import { useState } from 'react';
import DetalhesContrato from './DetalhesContrato';
import FormularioContrato from './FormularioContrato';

export default function App() {
  const [modo, setModo] = useState('cadastro'); // cadastro | lista | detalhes
  const [contratoSelecionado, setContratoSelecionado] = useState(null);

  const handleSalvarContrato = async (dadosContrato) => {
    try {
      // Prepara os dados para salvar
      const dadosParaSalvar = {
        ...dadosContrato,
        valorGlobal: parseFloat(dadosContrato.valorGlobal.replace(',', '.')),
        valorComissao: parseFloat(dadosContrato.valorComissao),
        dataCriacao: Timestamp.now(),
      };

      if (contratoSelecionado && contratoSelecionado.id) {
        // Atualizar contrato existente
        const ref = doc(db, 'contratos', contratoSelecionado.id);
        await updateDoc(ref, dadosParaSalvar);
        alert('Contrato atualizado com sucesso!');
      } else {
        // Novo contrato
        await addDoc(collection(db, 'contratos'), dadosParaSalvar);
        alert('Contrato salvo com sucesso!');
      }

      setContratoSelecionado(null);
      setModo('lista');
    } catch (error) {
      console.error('Erro ao salvar contrato:', error);
      alert('Erro ao salvar contrato');
    }
  };

  const handleCancelarFormulario = () => {
    setContratoSelecionado(null);
    setModo('lista');
  };

  // Renderização condicional com base no modo
  if (modo === 'cadastro') {
    return (
      <FormularioContrato
        contrato={contratoSelecionado}
        onSalvar={handleSalvarContrato}
        onCancelar={handleCancelarFormulario}
      />
    );
  }

  if (modo === 'detalhes') {
    return (
      <DetalhesContrato
        contrato={contratoSelecionado}
        onVoltar={() => setModo('lista')}
        onEditar={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('cadastro');
        }}
      />
    );
  }

  if (modo === 'lista') {
    return (
      <ListaContratos
        onVoltar={() => {
          setContratoSelecionado(null);
          setModo('cadastro');
        }}
        onEditar={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('cadastro');
        }}
        onVer={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('detalhes');
        }}
      />
    );
  }

  return null; // fallback, não deve chegar aqui
}
