import React, { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, getDocs, deleteDoc, doc, onSnapshot } from 'firebase/firestore';

const estadosNomes = {
  AC: 'Acre', AL: 'Alagoas', AP: 'Amapá', AM: 'Amazonas', BA: 'Bahia',
  CE: 'Ceará', DF: 'Distrito Federal', ES: 'Espírito Santo', GO: 'Goiás',
  MA: 'Maranhão', MT: 'Mato Grosso', MS: 'Mato Grosso do Sul', MG: 'Minas Gerais',
  PA: 'Pará', PB: 'Paraíba', PR: 'Paraná', PE: 'Pernambuco', PI: 'Piauí',
  RJ: 'Rio de Janeiro', RN: 'Rio Grande do Norte', RS: 'Rio Grande do Sul',
  RO: 'Rondônia', RR: 'Roraima', SC: 'Santa Catarina', SP: 'São Paulo',
  SE: 'Sergipe', TO: 'Tocantins'
};

export default function ListaContratos({ onVoltar, onEditar, onVer }) {
  const [contratos, setContratos] = useState([]);
  const [filtro, setFiltro] = useState('');

  useEffect(() => {
    const contratosRef = collection(db, 'contratos');
    const unsubscribe = onSnapshot(contratosRef, (snapshot) => {
      const lista = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setContratos(lista);
    });
    return () => unsubscribe();
  }, []);

  const excluirContrato = async (id) => {
    if (!window.confirm('Deseja excluir este contrato?')) return;
    try {
      await deleteDoc(doc(db, 'contratos', id));
      alert('Contrato excluído com sucesso.');
    } catch (error) {
      console.error('Erro ao excluir contrato:', error);
    }
  };

  const contratosFiltrados = contratos.filter(c =>
    (c.cliente + c.estado).toLowerCase().includes(filtro.toLowerCase())
  );

  return (
    <div className="max-w-7xl mx-auto p-4 bg-white rounded shadow">
      <h1 className="text-2xl font-bold mb-4">Contratos</h1>

      <input
        type="text"
        placeholder="Buscar por cliente ou estado"
        value={filtro}
        onChange={(e) => setFiltro(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />

      <table className="w-full border-collapse border border-gray-300 text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-2 py-1">Cliente</th>
            <th className="border px-2 py-1">Estado</th>
            <th className="border px-2 py-1">Valor Global</th>
            <th className="border px-2 py-1">Comissão</th>
            <th className="border px-2 py-1">Ações</th>
          </tr>
        </thead>
        <tbody>
          {contratosFiltrados.length === 0 ? (
            <tr>
              <td colSpan="5" className="text-center p-4">Nenhum contrato encontrado.</td>
            </tr>
          ) : (
            contratosFiltrados.map(c => (
              <tr key={c.id} className="text-center">
                <td className="border px-2 py-1">{c.cliente}</td>
                <td className="border px-2 py-1">{estadosNomes[c.estado] || c.estado}</td>
                <td className="border px-2 py-1">R$ {parseFloat(c.valorGlobal).toFixed(2)}</td>
                <td className="border px-2 py-1">R$ {parseFloat(c.valorComissao).toFixed(2)}</td>
                <td className="border px-2 py-1 space-x-1">
                  <button
                    className="bg-blue-600 hover:bg-blue-700 text-white px-2 py-1 rounded"
                    onClick={() => onVer(c)}
                  >
                    Ver
                  </button>
                  <button
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-2 py-1 rounded"
                    onClick={() => onEditar(c)}
                  >
                    Editar
                  </button>
                  <button
                    className="bg-red-600 hover:bg-red-700 text-white px-2 py-1 rounded"
                    onClick={() => excluirContrato(c.id)}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <button
        onClick={onVoltar}
        className="mt-4 bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded"
      >
        Voltar
      </button>
    </div>
  );
}
