import { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, query, where, onSnapshot, Timestamp } from 'firebase/firestore';

export default function Dashboard({ onNovoContrato, onEditar, onVer }) {
  const [contratos, setContratos] = useState([]);

  useEffect(() => {
    // Listener em tempo real para todos os contratos
    const contratosRef = collection(db, 'contratos');
    const unsubscribe = onSnapshot(contratosRef, (snapshot) => {
      const lista = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setContratos(lista);
    });
    return () => unsubscribe();
  }, []);

  // Filtra contratos ativos pelo campo encerramento maior que hoje
  const hoje = new Date();
  const contratosAtivos = contratos.filter(c => {
    if (!c.encerramento) return false;
    const encerramento = new Date(c.encerramento);
    return encerramento >= hoje;
  });

  // Total de vendas no mês (soma de valorGlobal dos contratos criados no mês atual)
  const inicioMes = new Date(hoje.getFullYear(), hoje.getMonth(), 1);
  const vendasMes = contratos.reduce((acc, c) => {
    if (!c.dataCriacao) return acc;
    const dataCriacao = c.dataCriacao.toDate ? c.dataCriacao.toDate() : new Date(c.dataCriacao);
    if (dataCriacao >= inicioMes && dataCriacao <= hoje) {
      return acc + (parseFloat(c.valorGlobal) || 0);
    }
    return acc;
  }, 0);

  // Total comissões
  const comissoesTotais = contratos.reduce((acc, c) => acc + (parseFloat(c.valorComissao) || 0), 0);

  // Total plataformas
  const totalPlataformas = contratos.reduce((acc, c) => acc + (parseInt(c.qtdPlataformas) || 0), 0);

  // Total elevadores
  const totalElevadores = contratos.reduce((acc, c) => acc + (parseInt(c.qtdElevadores) || 0), 0);

  return (
    <div className="min-h-screen bg-gray-900 p-6 text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">Gestão dos Contratos</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6 mb-8">
        <Card title="Vendas no mês" valor={`R$ ${vendasMes.toFixed(2).replace('.', ',')}`} cor="bg-blue-700" />
        <Card title="Contratos ativos" valor={contratosAtivos.length} cor="bg-green-700" />
        <Card title="Comissões totais" valor={`R$ ${comissoesTotais.toFixed(2).replace('.', ',')}`} cor="bg-yellow-700" />
        <Card title="Total Plataformas" valor={totalPlataformas} cor="bg-purple-700" />
        <Card title="Total Elevadores" valor={totalElevadores} cor="bg-pink-700" />
      </div>

      <div className="mb-6 text-center">
        <button
          onClick={onNovoContrato}
          className="bg-indigo-600 hover:bg-indigo-700 px-6 py-3 rounded-xl font-semibold text-white transition"
        >
          Novo Contrato
        </button>
      </div>

      <div className="overflow-x-auto bg-gray-800 rounded-xl shadow p-4">
        <table className="min-w-full border border-gray-700 text-sm">
          <thead className="bg-gray-700 text-white">
            <tr>
              <th className="px-3 py-2 border border-gray-600">Cliente</th>
              <th className="px-3 py-2 border border-gray-600">Estado</th>
              <th className="px-3 py-2 border border-gray-600">Valor Global</th>
              <th className="px-3 py-2 border border-gray-600">Comissão</th>
              <th className="px-3 py-2 border border-gray-600">Início</th>
              <th className="px-3 py-2 border border-gray-600">Encerramento</th>
              <th className="px-3 py-2 border border-gray-600">Ações</th>
            </tr>
          </thead>
          <tbody>
            {contratos.map((c) => (
              <tr key={c.id} className="text-center even:bg-gray-700 odd:bg-gray-800">
                <td className="px-3 py-2 border border-gray-600">{c.cliente}</td>
                <td className="px-3 py-2 border border-gray-600">{c.estado}</td>
                <td className="px-3 py-2 border border-gray-600">
                  R$ {(parseFloat(c.valorGlobal) || 0).toLocaleString('pt-BR', { minimumFractionDigits: 2 })}
                </td>
                <td className="px-3 py-2 border border-gray-600">
                  R$ {(parseFloat(c.valorComissao) || 0).toLocaleString('pt-BR', { minimumFractionDigits: 2 })}
                </td>
                <td className="px-3 py-2 border border-gray-600">{c.inicio}</td>
                <td className="px-3 py-2 border border-gray-600">{c.encerramento}</td>
                <td className="px-3 py-2 border border-gray-600 space-x-1">
                  <button
                    onClick={() => onVer(c)}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-2 py-1 rounded text-xs"
                  >
                    Ver
                  </button>
                  <button
                    onClick={() => onEditar(c)}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-2 py-1 rounded text-xs"
                  >
                    Editar
                  </button>
                </td>
              </tr>
            ))}
            {contratos.length === 0 && (
              <tr>
                <td colSpan="7" className="text-center py-4 text-gray-400">
                  Nenhum contrato cadastrado.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function Card({ title, valor, cor }) {
  return (
    <div className={`${cor} rounded-xl p-6 shadow-lg flex flex-col justify-center items-center`}>
      <h3 className="text-lg font-semibold mb-2">{title}</h3>
      <p className="text-2xl font-bold">{valor}</p>
    </div>
  );
}
