import React from 'react';

export default function FormularioContrato({
  contrato = {},
  onSalvar,
  onCancelar,
  onChangeContrato,
}) {
  // Como o estado está no componente pai, usamos onChangeContrato para atualizar

  // Função para tratar mudanças nos inputs e repassar para o pai
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    onChangeContrato && onChangeContrato(name, val);
  };

  return (
    <main className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto bg-white p-6 rounded-xl shadow-md">
        <h1 className="text-2xl font-bold mb-4">
          {contrato?.id ? 'Editar Contrato' : 'Cadastro de Contrato'}
        </h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Input
            label="Órgão / Cliente"
            name="cliente"
            value={contrato.cliente || ''}
            onChange={handleChange}
          />

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Estado
            </label>
            <select
              name="estado"
              value={contrato.estado || ''}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
            >
              <option value="">Selecione um estado</option>
              <option value="AC">Acre</option>
              <option value="AL">Alagoas</option>
              <option value="AP">Amapá</option>
              <option value="AM">Amazonas</option>
              <option value="BA">Bahia</option>
              <option value="CE">Ceará</option>
              <option value="DF">Distrito Federal</option>
              <option value="ES">Espírito Santo</option>
              <option value="GO">Goiás</option>
              <option value="MA">Maranhão</option>
              <option value="MT">Mato Grosso</option>
              <option value="MS">Mato Grosso do Sul</option>
              <option value="MG">Minas Gerais</option>
              <option value="PA">Pará</option>
              <option value="PB">Paraíba</option>
              <option value="PR">Paraná</option>
              <option value="PE">Pernambuco</option>
              <option value="PI">Piauí</option>
              <option value="RJ">Rio de Janeiro</option>
              <option value="RN">Rio Grande do Norte</option>
              <option value="RS">Rio Grande do Sul</option>
              <option value="RO">Rondônia</option>
              <option value="RR">Roraima</option>
              <option value="SC">Santa Catarina</option>
              <option value="SP">São Paulo</option>
              <option value="SE">Sergipe</option>
              <option value="TO">Tocantins</option>
            </select>
          </div>

          <Input
            label="Valor Global (R$)"
            name="valorGlobal"
            value={contrato.valorGlobal || ''}
            onChange={handleChange}
          />

          <Input
            label="Valor da Comissão (4%)"
            name="valorComissao"
            value={contrato.valorComissao || ''}
            disabled
          />

          <Input
            label="Objeto do Contrato"
            name="objeto"
            value={contrato.objeto || ''}
            onChange={handleChange}
          />
          <Input
            label="Quantidade de Elevadores"
            name="qtdElevadores"
            value={contrato.qtdElevadores || ''}
            onChange={handleChange}
          />
          <Input
            label="Quantidade de Plataformas"
            name="qtdPlataformas"
            value={contrato.qtdPlataformas || ''}
            onChange={handleChange}
          />
          <Input
            label="Data de Início"
            name="inicio"
            type="date"
            value={contrato.inicio || ''}
            onChange={handleChange}
          />
          <Input
            label="Data de Encerramento"
            name="encerramento"
            type="date"
            value={contrato.encerramento || ''}
            onChange={handleChange}
          />
          <Input
            label="Gestor (opcional)"
            name="gestor"
            value={contrato.gestor || ''}
            onChange={handleChange}
          />
          <Input
            label="Telefone (opcional)"
            name="telefone"
            value={contrato.telefone || ''}
            onChange={handleChange}
          />
          <Input
            label="E-mail (opcional)"
            name="email"
            value={contrato.email || ''}
            onChange={handleChange}
          />

          <div className="col-span-1 md:col-span-2">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                name="necessitaArt"
                checked={contrato.necessitaArt || false}
                onChange={handleChange}
              />
              Necessita de ART
            </label>
          </div>

          <div className="col-span-1 md:col-span-2 mt-4 flex gap-4">
            <button
              onClick={onSalvar}
              className="flex-1 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-xl"
            >
              {contrato?.id ? 'Atualizar Contrato' : 'Salvar Contrato'}
            </button>
            <button
              onClick={onCancelar}
              className="flex-1 bg-gray-300 hover:bg-gray-400 text-black font-semibold py-2 px-4 rounded-xl"
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>
    </main>
  );
}

function Input({ label, name, value, onChange, type = 'text', disabled = false }) {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
      <input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        disabled={disabled}
        className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
      />
    </div>
  );
}
