import { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, getDocs } from 'firebase/firestore';

export default function ListaContratos({ onVoltar }) {
  const [contratos, setContratos] = useState([]);

  useEffect(() => {
    const carregarContratos = async () => {
      const contratosRef = collection(db, 'contratos');
      const snapshot = await getDocs(contratosRef);
      const lista = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setContratos(lista);
    };

    carregarContratos();
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">Contratos Cadastrados</h1>
        <div className="overflow-x-auto bg-white shadow rounded-lg">
          <table className="min-w-full border">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-4 py-2 border">Cliente</th>
                <th className="px-4 py-2 border">Estado</th>
                <th className="px-4 py-2 border">Valor Global (R$)</th>
                <th className="px-4 py-2 border">Comissão (R$)</th>
                <th className="px-4 py-2 border">Início</th>
                <th className="px-4 py-2 border">Encerramento</th>
              </tr>
            </thead>
            <tbody>
              {contratos.map((c) => (
                <tr key={c.id} className="text-sm text-center">
                  <td className="px-4 py-2 border">{c.cliente}</td>
                  <td className="px-4 py-2 border">{c.estado}</td>
                  <td className="px-4 py-2 border">{parseFloat(c.valorGlobal).toFixed(2)}</td>
                  <td className="px-4 py-2 border">{parseFloat(c.valorComissao).toFixed(2)}</td>
                  <td className="px-4 py-2 border">{c.inicio}</td>
                  <td className="px-4 py-2 border">{c.encerramento}</td>
                </tr>
              ))}
              {contratos.length === 0 && (
                <tr>
                  <td colSpan="6" className="px-4 py-2 text-center text-gray-500">Nenhum contrato encontrado.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <div className="mt-6">
          <button
            onClick={onVoltar}
            className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-xl"
          >
            Voltar para Cadastro
          </button>
        </div>
      </div>
    </div>
  );
}
