import { useState } from 'react';
import Dashboard from './Dashboard';
import ListaContratos from './ListaContratos';
import FormularioContrato from './FormularioContrato';
import DetalhesContrato from './DetalhesContrato';

export default function App() {
  const [modo, setModo] = useState('dashboard'); // 'dashboard', 'formulario', 'detalhes'
  const [contratoSelecionado, setContratoSelecionado] = useState(null);
  const [modoEdicao, setModoEdicao] = useState(false);

  // Abrir formulário para novo ou editar
  const abrirFormulario = (contrato = null) => {
    setContratoSelecionado(contrato);
    setModoEdicao(!!contrato);
    setModo('formulario');
  };

  // Abrir tela de detalhes
  const abrirDetalhes = (contrato) => {
    setContratoSelecionado(contrato);
    setModo('detalhes');
  };

  // Voltar para dashboard (que contém lista)
  const voltarDashboard = () => {
    setContratoSelecionado(null);
    setModoEdicao(false);
    setModo('dashboard');
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4">
      {modo === 'dashboard' && (
        <>
          <Dashboard onNovoContrato={() => abrirFormulario()} />
          <ListaContratos
            onEditar={abrirFormulario}
            onVer={abrirDetalhes}
            // Não precisa de onVoltar, pois estamos na dashboard
          />
        </>
      )}

      {modo === 'formulario' && (
        <FormularioContrato
          contrato={contratoSelecionado}
          modoEdicao={modoEdicao}
          onVoltar={voltarDashboard}
        />
      )}

      {modo === 'detalhes' && (
        <DetalhesContrato
          contrato={contratoSelecionado}
          onVoltar={voltarDashboard}
          onEditar={abrirFormulario}
        />
      )}
    </div>
  );
}
