import { db } from './firebase';
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  Timestamp,
} from 'firebase/firestore';
import ListaContratos from './ListaContratos';
import { useState } from 'react';
import DetalhesContrato from './DetalhesContrato';
import FormularioContrato from './FormularioContrato';

const contratoInicial = {
  cliente: '',
  estado: '',
  valorGlobal: '',
  valorComissao: '',
  objeto: '',
  qtdElevadores: '',
  qtdPlataformas: '',
  inicio: '',
  encerramento: '',
  gestor: '',
  telefone: '',
  email: '',
  necessitaArt: false,
};

export default function App() {
  const [editandoContrato, setEditandoContrato] = useState(null);
  const [modo, setModo] = useState('cadastro'); // cadastro | lista | detalhes
  const [contratoSelecionado, setContratoSelecionado] = useState(null);

  const handleSave = async (dadosContrato) => {
    try {
      const contratoParaSalvar = {
        ...dadosContrato,
        valorGlobal: parseFloat(dadosContrato.valorGlobal.toString().replace(',', '.')),
        valorComissao: parseFloat(dadosContrato.valorComissao),
        dataCriacao: Timestamp.now(),
      };

      if (editandoContrato) {
        const ref = doc(db, 'contratos', editandoContrato.id);
        await updateDoc(ref, contratoParaSalvar);
        alert('Contrato atualizado com sucesso!');
      } else {
        await addDoc(collection(db, 'contratos'), contratoParaSalvar);
        alert('Contrato salvo com sucesso!');
      }

      setEditandoContrato(null);
      setContratoSelecionado(null);
      setModo('lista');
    } catch (error) {
      console.error('Erro ao salvar contrato:', error);
      alert('Erro ao salvar contrato');
    }
  };

  const iniciarEdicao = (contrato) => {
    setContratoSelecionado(contrato);
    setEditandoContrato(contrato);
    setModo('cadastro');
  };

  if (modo === 'cadastro') {
    return (
      <FormularioContrato
        contrato={contratoSelecionado || contratoInicial}
        onSalvar={handleSave}
        onCancelar={() => {
          setModo('lista');
          setContratoSelecionado(null);
          setEditandoContrato(null);
        }}
      />
    );
  }

  if (modo === 'detalhes') {
    return (
      <DetalhesContrato
        contrato={contratoSelecionado}
        onVoltar={() => setModo('lista')}
        onEditar={(contrato) => iniciarEdicao(contrato)}
      />
    );
  }

  if (modo === 'lista') {
    return (
      <ListaContratos
        onVoltar={() => {
          setModo('cadastro');
          setContratoSelecionado(null);
          setEditandoContrato(null);
        }}
        onEditar={iniciarEdicao}
        onVer={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('detalhes');
        }}
      />
    );
  }

  return null; // fallback caso modo seja inválido
}
