import ListaContratos from './ListaContratos';
import { useState } from 'react';
import { db } from './firebase';
import { collection, addDoc, Timestamp } from 'firebase/firestore';

export default function App() {
  const [contrato, setContrato] = useState({
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    const updatedContrato = { ...contrato, [name]: val };

    if (name === 'valorGlobal') {
      const num = parseFloat(val.replace(',', '.')) || 0;
      updatedContrato.valorComissao = (num * 0.04).toFixed(2);
    }

    setContrato(updatedContrato);
  };

  const handleSave = async () => {
    try {
      await addDoc(collection(db, 'contratos'), {
        ...contrato,
        valorGlobal: parseFloat(contrato.valorGlobal.replace(',', '.')),
        valorComissao: parseFloat(contrato.valorComissao),
        dataCriacao: Timestamp.now(),
      });
      alert('Contrato salvo com sucesso!');
      setContrato({
        cliente: '',
        estado: '',
        valorGlobal: '',
        valorComissao: '',
        objeto: '',
        qtdElevadores: '',
        qtdPlataformas: '',
        inicio: '',
        encerramento: '',
        gestor: '',
        telefone: '',
        email: '',
        necessitaArt: false,
      });
    } catch (error) {
      console.error('Erro ao salvar contrato:', error);
      alert('Erro ao salvar contrato');
    }
  };

  const [mostrarLista, setMostrarLista] = useState(false);

if (mostrarLista) {
  return <ListaContratos onVoltar={() => setMostrarLista(false)} />;
}
  return (
    <main className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">Cadastro de Contrato</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Input label="Órgão / Cliente" name="cliente" value={contrato.cliente} onChange={handleChange} />
          <Input label="Estado" name="estado" value={contrato.estado} onChange={handleChange} />
          <Input label="Valor Global (R$)" name="valorGlobal" value={contrato.valorGlobal} onChange={handleChange} />
          <Input label="Valor da Comissão (4%)" name="valorComissao" value={contrato.valorComissao} disabled />
          <Input label="Objeto do Contrato" name="objeto" value={contrato.objeto} onChange={handleChange} />
          <Input label="Quantidade de Elevadores" name="qtdElevadores" value={contrato.qtdElevadores} onChange={handleChange} />
          <Input label="Quantidade de Plataformas" name="qtdPlataformas" value={contrato.qtdPlataformas} onChange={handleChange} />
          <Input label="Data de Início" name="inicio" type="date" value={contrato.inicio} onChange={handleChange} />
          <Input label="Data de Encerramento" name="encerramento" type="date" value={contrato.encerramento} onChange={handleChange} />
          <Input label="Gestor (opcional)" name="gestor" value={contrato.gestor} onChange={handleChange} />
          <Input label="Telefone (opcional)" name="telefone" value={contrato.telefone} onChange={handleChange} />
          <Input label="E-mail (opcional)" name="email" value={contrato.email} onChange={handleChange} />
          <div className="col-span-1 md:col-span-2">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                name="necessitaArt"
                checked={contrato.necessitaArt}
                onChange={handleChange}
              />
              Necessita de ART
            </label>
          </div>
          <div className="col-span-1 md:col-span-2 mt-4">
            <button
              onClick={handleSave}
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-xl"
            >
              Salvar Contrato
            </button>
          </div>
        </div>
      </div>
    </main>
  );
}

function Input({ label, name, value, onChange, type = 'text', disabled = false }) {
  return (
    <div className="col-span-1 md:col-span-2 mt-4 flex gap-4">
  <button
    onClick={handleSave}
    className="flex-1 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-xl"
  >
    Salvar Contrato
  </button>
  <button
    onClick={() => setMostrarLista(true)}
    className="flex-1 bg-gray-300 hover:bg-gray-400 text-black font-semibold py-2 px-4 rounded-xl"
  >
    Ver Contratos
  </button>
</div>
  );
}
