import { useState, useEffect } from 'react';

export default function FormularioContrato({ contrato, onSalvar, onCancelar }) {
  const [formContrato, setFormContrato] = useState({
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  });

  // Sempre que o prop contrato mudar, atualiza o estado interno do formulário
  useEffect(() => {
    if (contrato) {
      setFormContrato({
        ...contrato,
        // Garante que valores numéricos estejam como string para os inputs
        valorGlobal: contrato.valorGlobal ? contrato.valorGlobal.toString() : '',
        valorComissao: contrato.valorComissao ? contrato.valorComissao.toString() : '',
        qtdElevadores: contrato.qtdElevadores ? contrato.qtdElevadores.toString() : '',
        qtdPlataformas: contrato.qtdPlataformas ? contrato.qtdPlataformas.toString() : '',
      });
    }
  }, [contrato]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;

    setFormContrato((prev) => {
      const novoContrato = { ...prev, [name]: val };

      // Calcula comissão automaticamente quando valorGlobal mudar
      if (name === 'valorGlobal') {
        const num = parseFloat(val.replace(',', '.')) || 0;
        novoContrato.valorComissao = (num * 0.04).toFixed(2);
      }

      return novoContrato;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validação básica (exemplo)
    if (!formContrato.cliente || !formContrato.estado) {
      alert('Preencha os campos obrigatórios: Cliente e Estado.');
      return;
    }

    onSalvar(formContrato);
  };

  return (
    <main className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl font-bold mb-4">
          {contrato && contrato.id ? 'Editar Contrato' : 'Cadastro de Contrato'}
        </h1>

        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Input label="Órgão / Cliente" name="cliente" value={formContrato.cliente} onChange={handleChange} required />
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Estado</label>
            <select
              name="estado"
              value={formContrato.estado}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
              required
            >
              <option value="">Selecione um estado</option>
              <option value="AC">Acre</option>
              <option value="AL">Alagoas</option>
              <option value="AP">Amapá</option>
              <option value="AM">Amazonas</option>
              <option value="BA">Bahia</option>
              <option value="CE">Ceará</option>
              <option value="DF">Distrito Federal</option>
              <option value="ES">Espírito Santo</option>
              <option value="GO">Goiás</option>
              <option value="MA">Maranhão</option>
              <option value="MT">Mato Grosso</option>
              <option value="MS">Mato Grosso do Sul</option>
              <option value="MG">Minas Gerais</option>
              <option value="PA">Pará</option>
              <option value="PB">Paraíba</option>
              <option value="PR">Paraná</option>
              <option value="PE">Pernambuco</option>
              <option value="PI">Piauí</option>
              <option value="RJ">Rio de Janeiro</option>
              <option value="RN">Rio Grande do Norte</option>
              <option value="RS">Rio Grande do Sul</option>
              <option value="RO">Rondônia</option>
              <option value="RR">Roraima</option>
              <option value="SC">Santa Catarina</option>
              <option value="SP">São Paulo</option>
              <option value="SE">Sergipe</option>
              <option value="TO">Tocantins</option>
            </select>
          </div>

          <Input
            label="Valor Global (R$)"
            name="valorGlobal"
            value={formContrato.valorGlobal}
            onChange={handleChange}
            required
          />

          <Input
            label="Valor da Comissão (4%)"
            name="valorComissao"
            value={formContrato.valorComissao}
            disabled
          />

          <Input label="Objeto do Contrato" name="objeto" value={formContrato.objeto} onChange={handleChange} />
          <Input label="Quantidade de Elevadores" name="qtdElevadores" value={formContrato.qtdElevadores} onChange={handleChange} />
          <Input label="Quantidade de Plataformas" name="qtdPlataformas" value={formContrato.qtdPlataformas} onChange={handleChange} />
          <Input label="Data de Início" name="inicio" type="date" value={formContrato.inicio} onChange={handleChange} />
          <Input label="Data de Encerramento" name="encerramento" type="date" value={formContrato.encerramento} onChange={handleChange} />
          <Input label="Gestor (opcional)" name="gestor" value={formContrato.gestor} onChange={handleChange} />
          <Input label="Telefone (opcional)" name="telefone" value={formContrato.telefone} onChange={handleChange} />
          <Input label="E-mail (opcional)" name="email" value={formContrato.email} onChange={handleChange} />

          <div className="col-span-1 md:col-span-2">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                name="necessitaArt"
                checked={formContrato.necessitaArt}
                onChange={handleChange}
              />
              Necessita de ART
            </label>
          </div>

          <div className="col-span-1 md:col-span-2 mt-4 flex gap-4">
            <button
              type="submit"
              className="flex-1 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-xl"
            >
              {contrato && contrato.id ? 'Atualizar Contrato' : 'Salvar Contrato'}
            </button>
            <button
              type="button"
              onClick={onCancelar}
              className="flex-1 bg-gray-300 hover:bg-gray-400 text-black font-semibold py-2 px-4 rounded-xl"
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </main>
  );
}

function Input({ label, name, value, onChange, type = 'text', disabled = false, required = false }) {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
      <input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        disabled={disabled}
        required={required}
        className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
      />
    </div>
  );
}
