import { db } from './firebase';
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  Timestamp,
} from 'firebase/firestore';
import ListaContratos from './ListaContratos';
import { useState } from 'react';
import DetalhesContrato from './DetalhesContrato';
import FormularioContrato from './FormularioContrato';
import Dashboard from './Dashboard';

export default function App() {
  const [editandoContrato, setEditandoContrato] = useState(null);
  const [modo, setModo] = useState('dashboard'); // dashboard | cadastro | lista | detalhes
  const [contratoSelecionado, setContratoSelecionado] = useState(null);

  const contratoInicial = {
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  };

  const [contrato, setContrato] = useState(contratoInicial);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    const updatedContrato = { ...contrato, [name]: val };

    if (name === 'valorGlobal') {
      const num = parseFloat(val.replace(',', '.')) || 0;
      updatedContrato.valorComissao = (num * 0.04).toFixed(2);
    }

    setContrato(updatedContrato);
  };

  const handleSave = async () => {
    try {
      const dadosContrato = {
        ...contrato,
        valorGlobal: parseFloat(contrato.valorGlobal.replace(',', '.')),
        valorComissao: parseFloat(contrato.valorComissao),
        dataCriacao: Timestamp.now(),
      };

      if (editandoContrato) {
        const ref = doc(db, 'contratos', editandoContrato.id);
        await updateDoc(ref, dadosContrato);
        alert('Contrato atualizado com sucesso!');
      } else {
        await addDoc(collection(db, 'contratos'), dadosContrato);
        alert('Contrato salvo com sucesso!');
      }

      setContrato(contratoInicial);
      setEditandoContrato(null);
    } catch (error) {
      console.error('Erro ao salvar contrato:', error);
      alert('Erro ao salvar contrato');
    }
  };

  if (modo === 'dashboard') {
    return <Dashboard onCadastrar={() => setModo('cadastro')} />;
  }

  if (modo === 'cadastro') {
    return (
      <FormularioContrato
        contrato={contratoSelecionado}
        onSalvar={() => {
          setModo('lista');
          setContratoSelecionado(null);
        }}
        onCancelar={() => {
          setModo('lista');
          setContratoSelecionado(null);
        }}
      />
    );
  }

  if (modo === 'detalhes') {
    return (
      <DetalhesContrato
        contrato={contratoSelecionado}
        onVoltar={() => setModo('lista')}
        onEditar={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('cadastro');
        }}
      />
    );
  }

  if (modo === 'lista') {
    return (
      <ListaContratos
        onVoltar={() => setModo('dashboard')}
        onEditar={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('cadastro');
        }}
        onVer={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('detalhes');
        }}
      />
    );
  }

  return null;
}

function Input({ label, name, value, onChange, type = 'text', disabled = false }) {
  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
      <input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        disabled={disabled}
        className="w-full p-2 border border-gray-300 rounded-xl shadow-sm"
      />
    </div>
  );
}
