import { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, onSnapshot } from 'firebase/firestore';

export default function Dashboard({ onIrParaCadastro, onIrParaLista }) {
  const [contratos, setContratos] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'contratos'), (snapshot) => {
      const lista = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setContratos(lista);
    });
    return () => unsubscribe();
  }, []);

  const totalVendas = contratos.reduce((acc, c) => acc + parseFloat(c.valorGlobal || 0), 0);
  const comissoesTotais = contratos.reduce((acc, c) => acc + parseFloat(c.valorComissao || 0), 0);
  const contratosAtivos = contratos.filter(c => !c.encerramento || new Date(c.encerramento) > new Date()).length;

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-center">Gestão dos Contratos</h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card titulo="Total de Vendas no Mês" valor={totalVendas} cor="bg-green-600" />
          <Card titulo="Contratos Ativos" valor={contratosAtivos} cor="bg-blue-600" />
          <Card titulo="Comissões Totais" valor={comissoesTotais} cor="bg-yellow-500" />
        </div>

        <div className="flex justify-center gap-4">
          <button
            onClick={onIrParaCadastro}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-xl font-semibold"
          >
            Novo Contrato
          </button>
          <button
            onClick={onIrParaLista}
            className="bg-gray-700 hover:bg-gray-600 text-white px-6 py-3 rounded-xl font-semibold"
          >
            Ver Lista de Contratos
          </button>
        </div>
      </div>
    </div>
  );
}

function Card({ titulo, valor, cor }) {
  return (
    <div className={`rounded-xl p-6 shadow ${cor}`}>
      <h2 className="text-lg font-semibold">{titulo}</h2>
      <p className="text-2xl mt-2">
        {typeof valor === 'number'
          ? valor.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })
          : valor}
      </p>
    </div>
  );
}
