import { db } from './firebase';
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  Timestamp,
  onSnapshot,
} from 'firebase/firestore';
import ListaContratos from './ListaContratos';
import { useState, useEffect } from 'react';
import DetalhesContrato from './DetalhesContrato';
import FormularioContrato from './FormularioContrato';

export default function App() {
  const [modo, setModo] = useState('dashboard'); // agora começa na dashboard
  const [contratoSelecionado, setContratoSelecionado] = useState(null);
  const [contratos, setContratos] = useState([]);

  // Dados para a dashboard
  const [totalVendasMes, setTotalVendasMes] = useState(0);
  const [contratosAtivos, setContratosAtivos] = useState(0);
  const [comissoesTotais, setComissoesTotais] = useState(0);

  const contratoInicial = {
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  };

  const [contrato, setContrato] = useState(contratoInicial);
  const [editandoContrato, setEditandoContrato] = useState(null);

  // Atualiza contratos em tempo real
  useEffect(() => {
    const contratosRef = collection(db, 'contratos');
    const unsubscribe = onSnapshot(contratosRef, (snapshot) => {
      const lista = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setContratos(lista);
    });
    return () => unsubscribe();
  }, []);

  // Calcula os dados da dashboard sempre que 'contratos' mudar
  useEffect(() => {
    const agora = new Date();
    const mesAtual = agora.getMonth(); // 0-11
    const anoAtual = agora.getFullYear();

    let totalVendas = 0;
    let ativos = 0;
    let totalComissoes = 0;

    contratos.forEach((c) => {
      // Parse datas para comparar
      const inicio = new Date(c.inicio);
      const encerramento = c.encerramento ? new Date(c.encerramento) : null;

      // Total de vendas no mês (considera contratos iniciados no mês e ano atual)
      if (inicio.getMonth() === mesAtual && inicio.getFullYear() === anoAtual) {
        totalVendas += parseFloat(c.valorGlobal) || 0;
      }

      // Contratos ativos: hoje está entre início e encerramento (ou sem encerramento)
      const hoje = new Date();
      const isAtivo = inicio <= hoje && (!encerramento || encerramento >= hoje);
      if (isAtivo) {
        ativos += 1;
        totalComissoes += parseFloat(c.valorComissao) || 0;
      }
    });

    setTotalVendasMes(totalVendas);
    setContratosAtivos(ativos);
    setComissoesTotais(totalComissoes);
  }, [contratos]);

  // ... Aqui o restante da lógica de cadastro, edição, etc.

  // Renderização condicional de modos
  if (modo === 'cadastro') {
    return (
      <FormularioContrato
        contrato={contratoSelecionado}
        onSalvar={() => {
          setModo('dashboard');
          setContratoSelecionado(null);
          setEditandoContrato(null);
          setContrato(contratoInicial);
        }}
        onCancelar={() => {
          setModo('dashboard');
          setContratoSelecionado(null);
          setEditandoContrato(null);
          setContrato(contratoInicial);
        }}
      />
    );
  }

  if (modo === 'detalhes') {
    return (
      <DetalhesContrato
        contrato={contratoSelecionado}
        onVoltar={() => setModo('dashboard')}
        onEditar={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('cadastro');
          setEditandoContrato(contrato);
        }}
      />
    );
  }

  if (modo === 'lista') {
    return (
      <ListaContratos
        onVoltar={() => setModo('dashboard')}
        onEditar={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('cadastro');
          setEditandoContrato(contrato);
        }}
        onVer={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('detalhes');
        }}
      />
    );
  }

  // Dashboard default
  return (
    <main className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold mb-6">Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow p-6 text-center">
            <h2 className="text-xl font-semibold mb-2">Total de Vendas no Mês</h2>
            <p className="text-2xl font-bold text-green-600">
              R$ {totalVendasMes.toLocaleString('pt-BR', { minimumFractionDigits: 2 })}
            </p>
          </div>
          <div className="bg-white rounded-xl shadow p-6 text-center">
            <h2 className="text-xl font-semibold mb-2">Contratos Ativos</h2>
            <p className="text-2xl font-bold text-blue-600">{contratosAtivos}</p>
          </div>
          <div className="bg-white rounded-xl shadow p-6 text-center">
            <h2 className="text-xl font-semibold mb-2">Comissões Totais</h2>
            <p className="text-2xl font-bold text-purple-600">
              R$ {comissoesTotais.toLocaleString('pt-BR', { minimumFractionDigits: 2 })}
            </p>
          </div>
        </div>

        <div className="flex gap-4">
          <button
            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-xl"
            onClick={() => setModo('cadastro')}
          >
            Novo Contrato
          </button>
          <button
            className="bg-gray-300 hover:bg-gray-400 text-black py-2 px-4 rounded-xl"
            onClick={() => setModo('lista')}
          >
            Ver Contratos
          </button>
        </div>
      </div>
    </main>
  );
}
