import { useState } from 'react';
import { db } from './firebase';
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  Timestamp,
} from 'firebase/firestore';

import Dashboard from './Dashboard';
import FormularioContrato from './FormularioContrato';
import DetalhesContrato from './DetalhesContrato';

export default function App() {
  const [modo, setModo] = useState('dashboard'); // dashboard | cadastro | detalhes
  const [contratoSelecionado, setContratoSelecionado] = useState(null);
  const [editandoContrato, setEditandoContrato] = useState(null);

  const contratoInicial = {
    cliente: '',
    estado: '',
    valorGlobal: '',
    valorComissao: '',
    objeto: '',
    qtdElevadores: '',
    qtdPlataformas: '',
    inicio: '',
    encerramento: '',
    gestor: '',
    telefone: '',
    email: '',
    necessitaArt: false,
  };

  const [contrato, setContrato] = useState(contratoInicial);

  // Atualiza os campos do formulário
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    const updatedContrato = { ...contrato, [name]: val };

    if (name === 'valorGlobal') {
      const num = parseFloat(val.replace(',', '.')) || 0;
      updatedContrato.valorComissao = (num * 0.04).toFixed(2);
    }

    setContrato(updatedContrato);
  };

  // Salva ou atualiza contrato no Firestore
  const handleSave = async () => {
    try {
      const dadosContrato = {
        ...contrato,
        valorGlobal: parseFloat(contrato.valorGlobal.replace(',', '.')),
        valorComissao: parseFloat(contrato.valorComissao),
        dataCriacao: Timestamp.now(),
      };

      if (editandoContrato) {
        const ref = doc(db, 'contratos', editandoContrato.id);
        await updateDoc(ref, dadosContrato);
        alert('Contrato atualizado com sucesso!');
      } else {
        await addDoc(collection(db, 'contratos'), dadosContrato);
        alert('Contrato salvo com sucesso!');
      }

      setContrato(contratoInicial);
      setEditandoContrato(null);
      setContratoSelecionado(null);
      setModo('dashboard');
    } catch (error) {
      console.error('Erro ao salvar contrato:', error);
      alert('Erro ao salvar contrato');
    }
  };

  // Abre o formulário para editar contrato selecionado
  const iniciarEdicao = (contrato) => {
    setContrato({
      ...contrato,
      valorGlobal: contrato.valorGlobal.toString(),
      valorComissao: contrato.valorComissao.toString(),
    });
    setEditandoContrato(contrato);
    setContratoSelecionado(contrato);
    setModo('cadastro');
  };

  // Abre o formulário para novo contrato
  const novoContrato = () => {
    setContrato(contratoInicial);
    setEditandoContrato(null);
    setContratoSelecionado(null);
    setModo('cadastro');
  };

  // Renderiza componentes baseado no modo
  if (modo === 'dashboard') {
    return (
      <Dashboard
        onNovoContrato={novoContrato}
        onEditar={iniciarEdicao}
        onVer={(contrato) => {
          setContratoSelecionado(contrato);
          setModo('detalhes');
        }}
      />
    );
  }

  if (modo === 'cadastro') {
    return (
      <FormularioContrato
        contrato={contrato}
        onChange={handleChange}
        onSalvar={handleSave}
        onCancelar={() => {
          setContrato(contratoInicial);
          setEditandoContrato(null);
          setContratoSelecionado(null);
          setModo('dashboard');
        }}
        editando={!!editandoContrato}
      />
    );
  }

  if (modo === 'detalhes') {
    return (
      <DetalhesContrato
        contrato={contratoSelecionado}
        onVoltar={() => setModo('dashboard')}
        onEditar={iniciarEdicao}
      />
    );
  }

  return null; // fallback (não deveria chegar aqui)
}
